import { useState, useEffect } from 'react'
import { useLocation } from "react-router-dom"
import Zone from "./Zone"
const CreateRestaurantView = () => {

    const [name, setName] = useState('')
    const [locationR, setLocationR] = useState('')
    const {state} = useLocation()
    const [zones, setZones] = useState([])
    const [selectedZones,setSelectedZones] = useState([])
    const admin = state.admin


    const fetchZones = async() => {
        const res = await fetch(`http://localhost:8080/foodpanda/zones`)
        const data = await res.json()
        //setZones(data.name)
        //console.log(data)
        //console.log(zones)
        return data;
    }

    
    useEffect(() => {
        const getZones = async () => {
            const zonesLocal = await fetchZones()
            setZones(zonesLocal)
        }
        getZones()
        
    }, [])



    const onClickSave = async() =>{
        
        var array = []
        var checkboxes = document.querySelectorAll('input[type=checkbox]:checked')
        for (var i = 0; i < checkboxes.length; i++) {
            selectedZones.push(checkboxes[i].value)
        }

        console.log(admin)
        console.log(name);
        console.log(locationR);
        console.log(selectedZones)


        /*var s = []
        for (var option of document.getElementById("myMulti").options){
                if (option.selected) {
                    s.push(option.value);
                }
        }

       console.log(s)*/

       

    
    }

    
    return(
        <div className='containerWithBorder'>
		<h1 className='coloredLoginTitle'>Create Restaurant</h1>
		<form className="add-form">
			<div className="form-control">
				<label> Restaurant Name </label>
				<input  type="text"
						placeholder="Enter name" value={name} onChange={(e) => setName(e.target.value)} />
			</div>
			<div className="form-control">
				<label> Location </label>
				<input type="text" placeholder="Enter Address" value={locationR} onChange={(e) => setLocationR(e.target.value)}/>
			</div>
            <div>
				<label> Zones </label>
                <form >
                    {zones.map((z,index) => (
                        <Zone key={index} zone = {z}/>
                    ))}
                </form>
			</div>

			</form>
      
				<div>
				
		      	<button onClick={(e) => onClickSave(e)} class="btn btn-primary btn-block" type="submit">Create </button>
				
				</div>
      </div>
        

    )
};

export default CreateRestaurantView
