import { useState, useEffect } from 'react'
import { useLocation } from "react-router-dom"
const CreateRestaurantView = () => {

    const [name, setName] = useState('')
    const [locationR, setLocationR] = useState('')
    const [specialities, setSpecialities] = useState([])
    const {state} = useLocation()
    const admin = state.admin

    useEffect(() => {

        const getSpecialities = async () => {
            const specialitiesFromLocal = await fetchZones()
            console.log(specialitiesFromLocal)
            setSpecialities(specialitiesFromLocal)
        }
        const data = getSpecialities()

        var newSelect=document.createElement('select');
        var index=0;
        for(var element in data){
            var opt = document.createElement("myMulti");
            opt.value= index;
            opt.innerHTML = element.name; 
            newSelect.appendChild(opt);
            index++;
        }
        console.log(newSelect)
    }, [])

    const fetchZones = async() => {
        const res = await fetch(`http://localhost:8080/foodpanda/zones`)
        const data = await res.json()
        return data;

    }


    const onClickSave = async() =>{
        console.log(admin)
        console.log(name);
        console.log(locationR);

        var s = []
        for (var option of document.getElementById("myMulti").options){
                if (option.selected) {
                    s.push(option.value);
                }
        }

       console.log(s)
    
    }

    
    return(
        <div className='containerWithBorder'>
		<h1 className='coloredLoginTitle'>Create Restaurant</h1>
		<form className="add-form">
			<div className="form-control">
				<label> Restaurant Name </label>
				<input  type="text"
						placeholder="Enter name" value={name} onChange={(e) => setName(e.target.value)} />
			</div>
			<div className="form-control">
				<label> Location </label>
				<input type="text" placeholder="enter" value={locationR} onChange={(e) => setLocationR(e.target.value)}/>
			</div>
            <div className="form-control">
				<label> Zones </label>
				<section>
                    <select multiple="multiple" id="myMulti" >
                    </select>
                 </section>
			</div>

			</form>
      
				<div>
				
		      	<button onClick={(e) => onClickSave(e)} class="btn btn-primary btn-block" type="submit">Create </button>
				
				</div>
      </div>
        

    )
};

export default CreateRestaurantView
