import { useState, useEffect } from 'react'
import { useLocation } from "react-router-dom"
import { useNavigate } from "react-router-dom"
import Order from './Order'
import Food from './Food'

const ClientViewOrders = () => {
    const {state} = useLocation()
    const [orders, setOrders] = useState([])
    const navigate = useNavigate()
    const[showFoods,setShowFoods] = useState(false)
    const[currentOrder,setCurrentOrder] = useState('')

    const fecthMenuCategories = async() => {
        const res = await fetch(`http://localhost:8080/foodpanda/orders-client/${state.client}`)
        const data = await res.json()
        console.log(data)
        return data
    }

    useEffect(() =>{
        const getOrders = async() => {
            const ordersLocal = await fecthMenuCategories()
            setOrders(ordersLocal)
        }
        getOrders()       
    },[])

    console.log(orders)
    console.log(showFoods)

    const onLogOutSubmit = (e) => {
        e.preventDefault()

        navigate('/', {
        })
    }

    const doSomething = ({order,e}) => {
        e.preventDefault()
        console.log("jhug")
        setShowFoods(!showFoods) 
        setCurrentOrder(order)

    }

    
    const onClickViewCart = (e) => {
        e.preventDefault()

        navigate('/', {
        })
    }

    return(
        <div>
            <div className = "row">
                 <div style={{color: '#800040', marginLeft : 50, fontSize : 50}}>FoodPanda</div>
                 <button className = "btn" style = {{marginLeft : 900}} type="submit" id='btn-log-out' onClick={(e) => onClickViewCart(e)}>
            View Cart
        </button>
                 <button className = "btn" style = {{marginLeft : 1000}} type="submit" id='btn-log-out' onClick={(e) => onLogOutSubmit(e)}>
                    Log out
                 </button>
            </div>
            <hr></hr>
            <div>
                <div className = "container-2">
                    <div>
                        <h1 style={{marginLeft : 10, color: '#800040'}} className="details">Menu </h1>
                        <br></br>
                        <div>
                            {orders?.map((order, index) => (
                                <Order key ={index} order = {order} onClick = {(e) => doSomething({order,e})} />
                            ))}
                        </div> 
                        {showFoods && <div className = "container-3">
                            {currentOrder.foods.map((food, index) => (
                                <Food key = {index} food = {food} />
                            ))}
                        </div>} 
                    </div>
                </div>
            </div>
      </div>


    )

}

export default ClientViewOrders
