import { useState } from "react"
import Food from "./Food"
const Order2 = ({order}) =>{

    const[showFoods,setShowFoods] = useState(false)
    const onClick = (e) =>{
        e.preventDefault()
        console.log(showFoods)
        console.log(order.foods)
        setShowFoods(!showFoods)
    }  

    const changeStatus = (e) =>{
        e.preventDefault()
        if(order.status = "PENDING" ){
            console.log("hello")
        }
    }  
    return(
        <div>
            <div className = "speciality" onClick={(e) => onClick(e)}>
                <h3>
                    {order.client}
                </h3>
                <h3>
                    {order.price + " RON"}
                </h3>
                <h3 >
                    {order.date + " at " + order.time}
                </h3>
                <h3 className="status2">
                    {order.status}
                </h3>
                <button class="btn btn-status" type="submit" onClick={(e) => changeStatus(e)}>Change Status</button>
            </div>
            {showFoods && <div className = "container-foods">
                            {order.foods.map((food, index) => (
                                <Food key = {index} food = {food} />
                            ))}
            </div>} 
        </div>
       
    )
}

export default Order2
