import { useState } from "react"
import Food from "./Food"
const Order2 = ({order}) =>{

    const[showFoods,setShowFoods] = useState(false)
    const[acceptDecline, setAcceptDecline] = useState(false)
    const[inDelivery,setInDelivery] = useState(false)
    const[delivered1,setDelivered1] = useState(false)

    const onClick = (e) =>{
        e.preventDefault()
        console.log(showFoods)
        console.log(order.foods)
        setShowFoods(!showFoods)
    }  

    const changeStatus = (e) =>{
        e.preventDefault()
        if(order.status = "PENDING"){
            setAcceptDecline(true)
            console.log("hello")
        }
        if(order.status = "ACCEPTED"){
            setInDelivery(true)
            console.log("hello1")
        }
        if(order.status = "IN DELIVERY"){
            setDelivered1(true)
            console.log("hello2")
        }
    }  

    const accept = (e) => {

    }

    const decline = (e) => {

    }

    const in_delivery = (e) => {

    }

    const delivered = (e) => {

    }


    return(
        <div>
            <div className = "speciality" onClick={(e) => onClick(e)}>
                <h3>
                    {order.client}
                </h3>
                <h3>
                    {order.price + " RON"}
                </h3>
                <h3 >
                    {order.date + " at " + order.time}
                </h3>
                <h3 className="status2">
                    {order.status}
                </h3>
               
            </div>
            <button class="btn btn-status" type="submit" onClick={(e) => changeStatus(e)}>Change Status</button>
            <hr></hr>
            {showFoods && <div className = "container-foods">
                            {order.foods.map((food, index) => (
                                <Food key = {index} food = {food} />
                            ))}
            </div>} 
            {acceptDecline && <div className = "container-foods">
            <button class="btn btn-status" type="submit" onClick={(e) => accept(e)}>Accept</button>
            <button class="btn btn-status" type="submit" onClick={(e) => decline(e)}>Decline</button>               
            </div>} 
            {inDelivery && <div className = "container-foods">
            <button class="btn btn-status" type="submit" onClick={(e) => accept(e)}>In Delivery</button>             
            </div>} 
            {delivered1 && <div className = "container-foods">
            <button class="btn btn-status" type="submit" onClick={(e) => accept(e)}>Delivered</button>             
            </div>} 
        </div>
       
    )
}

export default Order2
